(function(){
  const LIB_URL = "https://example.com/acu-story-utils.js?v=0.1";
  if (window.ACU) return;
  const s = document.createElement("script");
  s.src = LIB_URL; s.async = true;
  s.onload = () => console.log("ACU JS loaded");
  s.onerror = () => alert("Couldn’t load ACU JS – check the URL.");
  document.head.appendChild(s);
})();

(function(){
  const p = (typeof GetPlayer==='function'? GetPlayer():null);
  if(!p){ alert('No Storyline player found'); return; }
  const score = p.GetVar('Score') || 0;
  p.SetVar('Score', score + 10);
})();

(function(name='PulseMe', {duration=900, scale=1.08}={}){
  const el = document.querySelector('[aria-label="'+name+'"]');
  if(!el){ alert('Object not found: '+name); return; }
  el.style.transition = 'transform '+duration+'ms ease-in-out';
  let up = true;
  setInterval(()=>{ el.style.transform = up? 'scale('+scale+')':'scale(1)'; up=!up; }, duration);
})('PulseMe',{duration:900, scale:1.08});
